mapper.select:
select *
from qna
where user_id = #{user_id}
order by qna_num desc;

mapper.update:
update qna
set ans_content=#{ans_content}
where qna_num = #{qna_num}

update qna
set ans_content=null
where qna_num = #{qna_num}

